sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Get all add-ons in this offering
    var offeringGR = current.offering.getRefRecord();
    var addonsToOfferingsGR = new GlideRecord("x_1128327_cake_add_ons_to_offerings");
    addonsToOfferingsGR.addQuery("offering", offeringGR.getValue("sys_id"));
    addonsToOfferingsGR.query();

    // Calculate the price of all add-ons in this offering
    var total = 0;
    while (addonsToOfferingsGR.next()) {
        var addOnGR = addonsToOfferingsGR.add_on.getRefRecord();
        var price = parseFloat(addOnGR.getValue("price"));
        var quantity = addonsToOfferingsGR.getValue("quantity");
        total += (price * quantity);
    }

    // Update the ingredient cost of the good
    offeringGR.add_on_price = total.toFixed(2);
	offeringGR.update();

})(current, previous);
