sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Check to see if there is already an offering for this good
	const offeringGR = new GlideRecord("x_1128327_cake_offering");
	offeringGR.addQuery("name", current.getValue("name"));
	offeringGR.query();

	// If not, create some records
	if (!offeringGR.next()) {
		// Create a new offering record for this good
		const newOfferingGR = new GlideRecord("x_1128327_cake_offering");
		newOfferingGR.name = current.getValue("name");
		newOfferingGR.insert();

		// Create a new goods to offerings record for this good to offering relationship
		const goodToOfferingGR = new GlideRecord("x_1128327_cake_goods_to_offerings");
		goodToOfferingGR.good = current.getValue("sys_id");
		goodToOfferingGR.offering = newOfferingGR.getValue("sys_id");
		goodToOfferingGR.insert();
	}

})(current, previous);
