sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	const p = new GlideAjax('GetPropertyAjax');
	p.addParam('sysparm_name', 'getProperty');
	p.addParam('sysparm_property', 'x_1128327_cake.hourly_rate');
	p.getXMLAnswer(getProperty);

	function getProperty(answer) {
		const hourlyRate = parseFloat(answer);

		const prepDurationString = g_form.getValue("preparation_time");
		const durationParts = prepDurationString.split(" ")[1].split(":");
		const hours = parseInt(durationParts[0]);
		const minutes = parseInt(durationParts[1]);
		const totalHours = hours + (minutes/60);

		const prepCost = (hourlyRate * totalHours).toFixed(2);
		g_form.setValue("preparation_cost", prepCost);
	}



// 	const basePrice = g_form.getValue("base_price") * 1;
// 	const additionalCost = getPriceFromString(newValue);
// 	const deliveryPrice = getPriceFromString(g_form.getValue("delivery_price"));
// 	const discountAmount = getPriceFromString(g_form.getValue("discount_amount"));

// 	const newTotalPrice = (basePrice + deliveryPrice + additionalCost) - discountAmount;

// 	g_form.setValue("total_price", newTotalPrice);
}
