sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	const p = new GlideAjax('GetPropertyAjax');
	p.addParam('sysparm_name', 'getProperty');
	p.addParam('sysparm_property', 'x_1128327_cake.cost_per_mile');
	p.getXMLAnswer(getProperty);

	function getProperty(answer) {
		g_form.clearMessages();
		const costPerMile = parseFloat(answer);
		const miles = parseFloat(newValue.toString().split(",").join(""));

		g_form.setValue("delivery_price", (costPerMile * miles).toString());
	}

}
