sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	const getPriceFromString = (priceString) => {
		const parts = priceString.split(";");
		const decimalValue = parseFloat(parts[1]);
		return decimalValue;
	}
	const basePrice = g_form.getValue("base_price") * 1;
	const additionalCost = getPriceFromString(g_form.getValue("additional_cost"));
	const deliveryPrice = getPriceFromString(newValue);
	const discountAmount = getPriceFromString(g_form.getValue("discount_amount"));

	const newTotalPrice = (basePrice + deliveryPrice + additionalCost) - discountAmount;

	g_form.setValue("total_price", newTotalPrice.toFixed(2));
}
