sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Get all goods in this offering
    var offeringGR = current.offering.getRefRecord();
    var goodsToOfferingsGR = new GlideRecord("x_1128327_cake_goods_to_offerings");
    goodsToOfferingsGR.addQuery("offering", offeringGR.getValue("sys_id"));
    goodsToOfferingsGR.query();

    // Calculate the cost of all goods in this offering (prep cost + ingredient cost)
    var total = 0;
    while (goodsToOfferingsGR.next()) {
        var goodGR = goodsToOfferingsGR.good.getRefRecord();
		var prepCost = parseFloat(goodGR.getValue("preparation_cost"));
        var quantity = goodsToOfferingsGR.getValue("quantity");
        total += (prepCost * quantity);
    }

    // Update the ingredient cost of the good
    offeringGR.preparation_cost = total.toFixed(2);
	offeringGR.update();

})(current, previous);
