sys_script_include.script:
var GetSettingAjax = Class.create();
GetSettingAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getSetting: function() {
		var settingName = this.getParameter('sysparm_setting');

		var settingGR = new GlideRecord("x_1128327_cake_settings");
		settingGR.addQuery("name", settingName);
		settingGR.query();

		if (settingGR.next()) {
			return settingGR.getValue("value");
		} else {
			return undefined;
		}
	},

	type: 'GetSettingAjax'
});
